        self.url = 'swr1.htm'
        self.iswrcb = iswrcb
        self.dataset4a_data = dataset4a_data
        self.mxactr = 0
        #self.mxactr, self.layer_row_column_data = self.assign_layer_row_column_data(layer_row_column_data, 6+naux)
        self.options = options
        self.parent.add_package(self)
