def suggested_schedule_changes(schedule, override_timeseries, total_number_of_days):
    """
    There are at least two types of schedule change suggestions: those that result in the static analysis of the schedule
    (what the total scheduled off time is) and those that result from the use of the space. 
    """
    from optimization_checks import check_total_off_times_for_days_of_week, check_switch_events_with_schedule_per_day

    res = []
    res += check_total_off_times_for_days_of_week(schedule)
    res += check_switch_events_with_schedule_per_day(schedule, override_timeseries, total_number_of_days)

    return res