tinymce.PluginManager.add('oppsaudios', function(editor, url) {
    var plugin_onclick = function() {
        editor.windowManager.open({
            title: 'Áudios',
            url: '/admin/multimedias/audio/?pop=oppseditor',
            width: 800,
            height: 600,
            buttons: [{
                text: 'Fechar',
                onclick: 'close'
            }]
        });
    };

    editor.addButton('oppsaudios', {
        text: false,
        image: url + '/img/audio.png',
        tooltip: 'Inserir Áudio do Sistema',
        onclick: plugin_onclick
    });

    editor.addMenuItem('oppsaudios', {
        text: 'Inserir Áudio do Sistema',
        icon: false,
        context: 'insert',
        onclick: plugin_onclick
    });
});
