tinymce.PluginManager.add('oppsaudios', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('oppsaudios', {
        text: false,
        image: url + '/img/audio.png',
        tooltip: 'Inserir ﾃ「dio do Sistema',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'ﾃ「dios',
                url: '/admin/multimedias/audio/?pop=oppseditor',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Fechar',
                    onclick: 'close'
                }]
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('oppsaudios', {
        text: 'Inserir ﾃ「dio do Sistema',
        icon: false,
        context: 'insert',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'ﾃ「dios',
                url: '/admin/multimedias/audio/?pop=oppseditor',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Fechar',
                    onclick: 'close'
                }]
            });
        }
    });
});
