tinymce.PluginManager.add('oppsvideos', function(editor, url) {
    var plugin_onclick = function() {
        editor.windowManager.open({
            title: 'Vídeos',
            url: '/admin/multimedias/video/?pop=oppseditor',
            width: 800,
            height: 600,
            buttons: [{
                text: 'Fechar',
                onclick: 'close'
            }]
        });
    };

    editor.addButton('oppsvideos', {
        text: false,
        image: url + '/img/video.png',
        tooltip: 'Inserir Video do Sistema',
        onclick: plugin_onclick
    });

    editor.addMenuItem('oppsvideos', {
        text: 'Inserir Vídeo do Sistema',
        icon: false,
        context: 'insert',
        onclick: plugin_onclick
    });
});
