#include <stdio.h>

#include "instcommon.h"


int copy_to_pczztchar(TCHAR *x, char *src, int maxlen)
{
  int k;
  for (k = 0; k < maxlen-2 && src[k] != '\0'; ++k) {
    x[k] = src[k];
  }
  if (k >= maxlen-1) {
    // buffer size exceeded
    return -1;
  }
  // pczztchar requires double zero termination
  x[k] = '\0';
  x[k+1] = '\0';
  return 0;
}

void pause()
{
  printf("\nPress ENTER to continue...\n");
  getchar();
}
